import React from "react";
import Nav from "../Navbar";
import './index.css'
import { temperamentos } from "../../actions";
import { connect } from "react-redux";

export  function Form (props){
    const [state, setSate] = React.useState({
        nombre: "", 
        altura: "", 
        peso: "",
        añosVida: "", 
        temps: [], 
    })

React.useEffect(()=>
{
   if (props.Temperamentos.length === 0) props.temp(); 

})

    console.log(props.Temperamentos)
    return (
        <div className='form'>
           <Nav/>
           <h2>Crea tu propia raza de perrito</h2>
           <span>Nombre*</span>
           <input type="text" placeholder="Nombre" value={state.nombre} onChange={event => event.target.value}></input>
           <span>Altura*</span>
           <input type="text" placeholder="Altura" value={state.altura} onChange={event => event.target.value}></input>
           <span>Peso*</span>
           <input type="text" placeholder="Peso" value={state.peso} onChange={event => event.target.value}></input>
           <span>Años de vida</span>
           <input type="text" placeholder="Años de vida" value={state.añosVida} onChange={event => event.target.value}></input>
           <div className="Nav">Temperamentos 
               <ul id="lista">
                   {props.Temperamentos.map(temp => {return <li>
                   <button className="temps" key={temp.id} onClick={()=>{setSate({...state, temperamentos: temperamentos.concat(temp.id)})}}>{temp.temperamento}</button>
                   </li>
                   })}
               </ul>
           </div>
           <button id="boton">Enviar</button>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        Temperamentos: state.temperamentos,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        temp: () => dispatch(temperamentos())
    }
}

export default connect (mapStateToProps,mapDispatchToProps)(Form)
