import {GET_DOGS, NAME_ORDER, PESO_ORDER, SEARCH} from '../constants'

const initialState = {
    listaPerritos: [],
    searchResult: [],
    perritoRandom: {},
}

export const reducer =  (state = initialState, action) => {
    switch (action.type){
        case GET_DOGS:
            return {
                ...state, 
                listaPerritos: action.payload,
             }
        case NAME_ORDER: 
            return {
                ...state, 
                listaPerritos: state.listaPerritos.reverse(),
            }
        case PESO_ORDER: 
            return {
                ...state, 
                listaPerritos: action.payload,
            }
        case SEARCH: 
            return {
                ...state,
            }

        default: return state; 
    }
}

export default reducer;
