import React from "react";
import './index.css'
import {connect} from 'react-redux'
import {orderName, orderPeso, temperamentos} from '../../actions'
import { Link } from "react-router-dom";

export  function Filtro (props){
    const [state, setState] = React.useState({
        temp: [],
        raza: [],
    })
    function OrderName (){
        props.Name();
    }
    function orderPeso(){
        props.Peso(props.Perritos); 
    }
    return (
        <div className="filtro">
            <Link to= {'/home'}>
             <div className="orden">
                <h4>ordenar</h4>
                <button id='peso' onClick={orderPeso}>peso</button>
                <button id='nombre' onClick={OrderName}>nombre</button>
            </div>
            </Link>
            <div className="filtra">
                <h4>filtrar</h4>
                <div>
                    <h5>Temperamento</h5>
                    <nav>
                        <ul id="menutemp">
                            {props.Temperamentos.map()}
                            <li><button></button></li>
                        </ul>
                    </nav>
                </div>
                <div>
                    <h5>Raza</h5>
                    <nav>
                        <ul id="menuraza">
                        </ul>
                    </nav>
                </div>
            </div>     
        </div>
    )
}

const mapStateToProps = state => {
    return {
        Temperamentos: state.temperamentos,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        Name: () => dispatch(orderName()),
        Peso: (arreglo) => dispatch(orderPeso(arreglo)),
        temp: () => dispatch(temperamentos()),
    }
}

export default connect (mapStateToProps,mapDispatchToProps)(Filtro)
